scene.onHitWall(SpriteKind.unagitated, function (sprite2) {
    sprite2.setVelocity(0, -10)
    if (sprite2.kind() == SpriteKind.unagitated) {
        sprite2.setKind(SpriteKind.faller)
    }
})
controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    button_press = 0
    if (up == 0) {
        spriteFlipped()
        V.vy = -200
        V.ay = -240
    } else if (up == 1) {
        sprite()
        V.vy = 200
        V.ay = 240
    }
})
let hammerspike: Sprite = null
let enemyspike: Sprite = null
let level = 0
let button_press = 0
let V: Sprite = null
let spriteImage: Image[] = []
let up = 0
start()
game.onUpdateInterval(100, function () {
    if (up == 1) {
        sprite()
    } else if (up == 0) {
        spriteFlipped()
    }
})
// game update block for smasher
game.onUpdate(function () {
    if (level == 1) {
        if (V.x == hammerspike.x) {
            if (hammerspike.kind() == SpriteKind.faller) {
                hammerspike.vy = 150
                hammerspike.setKind(SpriteKind.unagitated)
            }
        }
    }
})
